if($("#top").length){var scrollTrigger=100,backToTop=function(){var a=$(window).scrollTop();if(a>scrollTrigger){$("#top").addClass("show")}else{$("#top").removeClass("show")}};backToTop();$(window).on("scroll",function(){backToTop()});$("#top").on("click",function(a){a.preventDefault();$("html,body").animate({scrollTop:0},700)})};